import { create } from 'zustand';
import type { AccountsItem } from './service';

// Accounts Store
// Store hanya handle STATE, tidak boleh panggil service langsung!

interface AccountsState {
  items: AccountsItem[];
  loading: boolean;
  error: string | null;
  selectedItem: AccountsItem | null;

  // Setter functions
  setItems: (items: AccountsItem[]) => void;
  setLoading: (loading: boolean) => void;
  setError: (error: string | null) => void;
  setSelectedItem: (item: AccountsItem | null) => void;
  clearError: () => void;
  reset: () => void;
  removeItemById: (id: string) => void;
}

export const useAccountsStore = create<AccountsState>((set) => ({
  // State
  items: [],
  loading: false,
  error: null,
  selectedItem: null,

  // Actions
  setItems: (items) => set({ items }),
  setLoading: (loading) => set({ loading }),
  setError: (error) => set({ error }),
  setSelectedItem: (selectedItem) => set({ selectedItem }),
  clearError: () => set({ error: null }),
  reset: () => set({
    items: [],
    loading: false,
    error: null,
    selectedItem: null
  }),
  removeItemById: (id: string) => 
    set((state) => {
      const newItems = state.items.filter(item => item.id !== id);
      const shouldClearSelected = state.selectedItem?.id === id;

      return {
        items: newItems,
        selectedItem: shouldClearSelected ? null : state.selectedItem
      };
    }),
}));

// Helper hook untuk common patterns
export function useAccounts() {
  const store = useAccountsStore();

  return {
    // State
    ...store,

    // Computed values
    getItemById: (id: string) => store.items.find(item => item.id === id),
    hasItems: store.items.length > 0,
    isEmpty: store.items.length === 0,

    // Actions dengan business logic
    selectItemById: (id: string) => {
      const item = store.items.find(item => item.id === id);
      store.setSelectedItem(item || null);
    },

    // Untuk integrasi dengan service layer
    updateLocalItem: (updatedItem: AccountsItem) => {
      const newItems = store.items.map(item => 
        item.id === updatedItem.id ? updatedItem : item
      );
      store.setItems(newItems);

      // Update selected item jika sama
      if (store.selectedItem?.id === updatedItem.id) {
        store.setSelectedItem(updatedItem);
      }
    },

    removeItemById: (id: string) => {
      store.removeItemById(id);
    },

    // Reset dengan konfirmasi
    resetWithConfirmation: () => {
      if (confirm('Are you sure you want to reset all data?')) {
        store.reset();
      }
    }
  };
}

import { useEffect } from 'react';
import { Card } from '../../shared/ui/components/Card';
import { Button } from '../../shared/ui/Button';
import { useAccountsStore } from './store';
// Service dipanggil dari PARENT component, bukan dari sini!

interface AccountsUIProps {
  // Data dan functions disediakan oleh parent
  items?: any[];
  onLoadData?: () => Promise<void>;
  onCreateItem?: (data: any) => Promise<void>;
  loading?: boolean;
  error?: string | null;
}

export function AccountsUI({ 
  items = [],
  onLoadData,
  onCreateItem,
  loading = false,
  error = null 
}: AccountsUIProps) {
  const store = useAccountsStore();

  // Update store ketika props berubah
  useEffect(() => {
    store.setItems?.(items);
  }, [items, store]);

  useEffect(() => {
    store.setLoading?.(loading);
  }, [loading, store]);

  useEffect(() => {
    store.setError?.(error);
  }, [error, store]);

  const handleRefresh = () => {
    if (onLoadData) {
      onLoadData();
    }
  };

  const handleCreate = () => {
    if (onCreateItem) {
      onCreateItem({ name: 'New Item' });
    }
  };

  if (store.loading) {
    return (
      <Card title="Accounts Module" variant="primary">
        <div style={{ textAlign: 'center', padding: '20px' }}>
          <p>Loading accounts data...</p>
        </div>
      </Card>
    );
  }

  if (store.error) {
    return (
      <Card title="Error" variant="warning">
        <p>Error: {store.error}</p>
        <Button onClick={() => store.clearError?.()}>Dismiss</Button>
        <Button onClick={handleRefresh} variant="secondary" style={{ marginLeft: '10px' }}>
          Retry
        </Button>
      </Card>
    );
  }

  return (
    <Card title="Accounts Module" variant="primary">
      <div style={{ marginBottom: '20px' }}>
        <div style={{ display: 'flex', gap: '10px', marginBottom: '15px' }}>
          <Button onClick={handleRefresh} disabled={store.loading}>
            üîÑ Refresh Data
          </Button>
          <Button onClick={handleCreate} variant="secondary" disabled={store.loading}>
            ‚ûï Create New
          </Button>
          <Button onClick={() => store.reset?.()} variant="danger">
            üóëÔ∏è Reset
          </Button>
        </div>

        <div style={{ background: '#f8f9fa', padding: '10px', borderRadius: '4px' }}>
          <p>
            <strong>Total Items:</strong> {store.items.length}
          </p>
          {store.selectedItem && (
            <p>
              <strong>Selected:</strong> {store.selectedItem.name}
            </p>
          )}
        </div>
      </div>

      {store.items.length === 0 ? (
        <div style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
          <p>No items found. Create one!</p>
        </div>
      ) : (
        <div>
          <h4 style={{ marginBottom: '10px' }}>Items List:</h4>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {store.items.map((item) => (
              <li 
                key={item.id} 
                style={{
                  padding: '10px',
                  borderBottom: '1px solid #eee',
                  cursor: 'pointer',
                  background: store.selectedItem?.id === item.id ? '#e7f3ff' : 'transparent'
                }}
                onClick={() => store.setSelectedItem?.(item)}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <div>
                    <strong>{item.name}</strong>
                    <div style={{ fontSize: '12px', color: '#666' }}>
                      ID: {item.id} ‚Ä¢ Created: {item.createdAt.toLocaleDateString()}
                    </div>
                  </div>
                  <Button
                    onClick={(e) => {
                      e.stopPropagation();
                      store.removeItemById?.(item.id);
                    }}
                    variant="danger"
                    style={{ padding: '2px 8px', fontSize: '12px' }}
                  >
                    Delete
                  </Button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </Card>
  );
}

